
$(".dataset-title").on("click",function(){if($(this).hasClass("open")==false){$(this).addClass("open");$(this).parent("h2").next(".content").slideDown("slow")}else{$(this).removeClass("open");$(this).parent("h2").next(".content").slideUp("slow")}});$(".delete-dataset-link").bind("click touch",function(){if(confirm("Den ausgewählten Datensatz wirklich löschen?")){var a={};a.action="DeleteDataset";a.timestamp=requestTimestamp;a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.host=host;a.userAgent=userAgent;a.uid=uid;a.id=$(this).attr("datasetid");a.accessLevel=accessLevel;$.ajax({type:"POST",data:{request:JSON.stringify(a)},success:function(b){if(parseInt(b)==1){window.location.href="/account"}else{$(".ajax-message").text(b)}},error:function(c,b){$(".ajax-message").text(c+b)}})}});$(".copy-password").bind("click touch",function(){var a="";var b={};b.action="DecryptPassword";b.timestamp=requestTimestamp;b.tk=token;b.ts=timestamp;b.ipaddress=ipaddress;b.host=host;b.userAgent=userAgent;b.uid=uid;b.accessLevel=accessLevel;b.id=$(this).parent(".row").parent(".content").parent(".dataset").children(".datasetID").val();$.ajax({async:false,type:"POST",data:{request:JSON.stringify(b)},url:getAjaxUrl(),success:function(d){a=d}});var c=$("<input>");$("body").append(c);c.val(a);console.log(c.val());c.select();document.execCommand("copy");c.remove()});