
$(".dataset-title").on("click",function(){if($(this).hasClass("open")==false){$(this).addClass("open");$(this).parent("h2").next(".content").slideDown("slow")}else{$(this).removeClass("open");$(this).parent("h2").next(".content").slideUp("slow")}});$(".delete-dataset-link").bind("click touch",function(){if(confirm("Den ausgewählten Datensatz wirklich löschen?")){var a={};a.action="DeleteDataset";a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.uid=uid;a.id=$(this).attr("datasetid");$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){if(parseInt(b)==1){window.location.href="/account"}else{$(".ajax-message").text(b)}},error:function(c,b){$(".ajax-message").text(c+b)}})}});