
$(document).ready(function(){$("#updateDatasetButton").on("click touch",function(){var a={};a.action="UpdateDataset";a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.uid=uid;a.id=document.getElementById("edit-dataset-id").value;a.title=document.getElementById("datasetTitle").value;a.login=document.getElementById("datasetLogin").value;a.password=document.getElementById("datasetPassword").value;a.url=document.getElementById("datasetURL").value;a.project=document.getElementById("datasetProject").value;$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){if(parseInt(b)==1){window.location.href="/account"}else{$(".ajax-message").text(b)}},error:function(c,b){$(".ajax-message").text(c+b)}})});$("#regeneratePasswordButton").bind("click touch",function(){var a={};a.action="GeneratePassword";a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.uid=uid;a.length=document.getElementById("passwordLengthBox").value;a.lowerCharacters=document.getElementById("lowerCharacters").checked!==false?1:0;a.highCharacters=document.getElementById("highCharacters").checked!==false?1:0;a.numbers=document.getElementById("numbers").checked!==false?1:0;a.specialChars=document.getElementById("specialchars").checked!==false?1:0;$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){$("#datasetPassword").val(b);$("#datasetPassword").text(b)},error:function(c,b){}})})});