
$(document).ready(function(){$("#requestNewPasswordButton").bind("click touch",function(){var a={};a.action="NewPassword";a.tk=token;a.ts=timestamp;a.timestamp=requestTimestamp;a.ipaddress=ipaddress;a.host=host;a.userAgent=userAgent;a.honeypot=document.getElementById("newPasswordHoneypot").value;a.username=document.getElementById("usernamePasswordReset").value;$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){if(parseInt(b)==1){window.location.href="/"}},error:function(c,b){$(".ajax-message").text(c+" "+b)}})})});