
$(document).ready(function(){$("#login-button").bind("click touch",function(){if($(".ajax-message").hasClass("login-failed")){$(".ajax-message").removeClass("login-failed");$(".ajax-message").text("")}var a={};a.username=document.getElementById("username-input").value;a.password=document.getElementById("password-input").value;a.action="Login";a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.host=host;a.userAgent=userAgent;$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){if(parseInt(b)==1){window.location.href="/account"}else{$(".ajax-message").addClass("login-failed");$(".ajax-message").text("Benutzername oder Passwort ist falsch!");setTimeout(function(){if($(".ajax-message").hasClass("login-failed")){$(".ajax-message").removeClass("login-failed");$(".ajax-message").text("")}},2000)}},error:function(c,b){$(".ajax-message").text(c+" "+b)}})})});