
$(document).ready(function(){$("#requestNewPasswordButton").bind("click touch",function(){if($(".ajax-message").hasClass("login-failed")){$(".ajax-message").removeClass("login-failed");$(".ajax-message").text("")}var a={};a.username=document.getElementById("usernamePasswordReset").value;a.honeypot=document.getElementById("newPasswordHoneypot").value;a.action="NewPassword";a.timestamp=requestTimestamp;a.tk=token;a.ts=timestamp;a.ipaddress=ipaddress;a.host=host;a.userAgent=userAgent;a.uid=uid;a.accessLevel=accessLevel;if(a.username==""){return false}$.ajax({type:"POST",data:{request:JSON.stringify(a)},url:getAjaxUrl(),success:function(b){if(parseInt(b)==1){window.location.href="/account"}else{$(".ajax-message").addClass("login-failed");$(".ajax-message").text(b);setTimeout(function(){if($(".ajax-message").hasClass("login-failed")){$(".ajax-message").removeClass("login-failed");$(".ajax-message").text("")}},2000)}},error:function(c,b){$(".ajax-message").text(c+" "+b)}})})});